#!/usr/bin/env pwsh
$basedir=Split-Path $MyInvocation.MyCommand.Definition -Parent

$exe=""
$pathsep=":"
$env_node_path=$env:NODE_PATH
$new_node_path="D:\NW_Faktoria_App4\nw-buddy\node_modules\.pnpm\env-cmd@10.1.0\node_modules\env-cmd\bin\node_modules;D:\NW_Faktoria_App4\nw-buddy\node_modules\.pnpm\env-cmd@10.1.0\node_modules\env-cmd\node_modules;D:\NW_Faktoria_App4\nw-buddy\node_modules\.pnpm\env-cmd@10.1.0\node_modules;D:\NW_Faktoria_App4\nw-buddy\node_modules\.pnpm\node_modules"
if ($PSVersionTable.PSVersion -lt "6.0" -or $IsWindows) {
  # Fix case when both the Windows and Linux builds of Node
  # are installed in the same directory
  $exe=".exe"
  $pathsep=";"
} else {
  $new_node_path="/mnt/d/NW_Faktoria_App4/nw-buddy/node_modules/.pnpm/env-cmd@10.1.0/node_modules/env-cmd/bin/node_modules:/mnt/d/NW_Faktoria_App4/nw-buddy/node_modules/.pnpm/env-cmd@10.1.0/node_modules/env-cmd/node_modules:/mnt/d/NW_Faktoria_App4/nw-buddy/node_modules/.pnpm/env-cmd@10.1.0/node_modules:/mnt/d/NW_Faktoria_App4/nw-buddy/node_modules/.pnpm/node_modules"
}
if ([string]::IsNullOrEmpty($env_node_path)) {
  $env:NODE_PATH=$new_node_path
} else {
  $env:NODE_PATH="$new_node_path$pathsep$env_node_path"
}

$ret=0
if (Test-Path "$basedir/node$exe") {
  # Support pipeline input
  if ($MyInvocation.ExpectingInput) {
    $input | & "$basedir/node$exe"  "$basedir/../env-cmd/bin/env-cmd.js" $args
  } else {
    & "$basedir/node$exe"  "$basedir/../env-cmd/bin/env-cmd.js" $args
  }
  $ret=$LASTEXITCODE
} else {
  # Support pipeline input
  if ($MyInvocation.ExpectingInput) {
    $input | & "node$exe"  "$basedir/../env-cmd/bin/env-cmd.js" $args
  } else {
    & "node$exe"  "$basedir/../env-cmd/bin/env-cmd.js" $args
  }
  $ret=$LASTEXITCODE
}
$env:NODE_PATH=$env_node_path
exit $ret
